def StartServer(path=SCRIPT_DIR, port=8000):
    '''
    Starts a Simple HTTP Server on port 8000.

    '''
    ServerHandler.Server(ServerHandler.ServerHandler)
    #server_path = os.path.join(SCRIPT_DIR, 'serve', 'heavymeta_simple_server.py')
