def popup_server_status(start=True):
    layout = [[sg.Text('Server:')],
               [sg.ProgressBar(max_value=10, orientation='h', size=(20, 20), key='progress_1')]]

    line_boxes = b''
    line_bubbles = b''
    ring_black_dots = b''
    ring_gray_segments = b''
    ring_lines = b''
    red_dots_ring = b''
    blue_dots = b''
    ring_blue = b''
    bar_striped = b''
    
    # list of all of the base64 GIFs
    gifs = [ring_blue, red_dots_ring, ring_black_dots, ring_gray_segments, ring_lines, blue_dots, red_dots_ring, bar_striped, line_boxes, line_bubbles]
    offset = 0
    gif = gifs[3]
    
    layout = [[sg.Image(data=gif, enable_events=True, background_color='white', key='-IMAGE-', right_click_menu=['UNUSED', ['Exit']], pad=0)],]

    window = sg.Window('ServerHandler', layout,
            no_titlebar=True,
            grab_anywhere=False,
            keep_on_top=True,
            #background_color='white',
            transparent_color='white' if sg.running_windows() else None,
            alpha_channel=.8,
            margins=(0,0))
    
    while True:                                     # Event Loop
        event, values = window.read(timeout=10)    # loop every 10 ms to show that the 100 ms value below is used for animation
        if event == 'Exit':
                  break
        if event in (sg.WIN_CLOSED, 'Exit', 'Cancel'):
            break
        # update the animation in the window
        window['-IMAGE-'].update_animation(gif,  time_between_frames=100)
    window.close()
    
